pragma solidity ^0.5.0;

import "./CrowdDonation.sol";

/**
* @dev Campaign Factory contract to generate Crowdfund smart contract to run Crowd fund
*/
contract DoneeCreator {

    // list of campaigns
    address[] public donees;

    /**
    * @dev Create new campaign and send default manager is caller
    * @param minContribution minimum money can contribute for project (in ETH)
    * @param description description of campaign, purpose of campaign
    */
    function createDonee(uint minContribution, uint goal, string memory description) public {
        address newDonee = address (new CrowdDonation(
            msg.sender,
            minContribution,
            goal,
            description
        ));

        donees.push(newDonee);
    }

    /**
    * @dev get all deployed campaigns
    */
    function getDeployedDonees() public view returns (address[] memory) {
        return donees;
    }
}

